function initAufDateiInformationen {
	# nach https://gkv-datenaustausch.de/media/dokumente/leistungserbringer_1/zahnaerzte/technische_anlagen___aktuell_1/20161208_TA_Version_3_8_oA.pdf Seite 26
	# 4.5.3 Datensatzbeschreibung Auftragssatz
	R="Routing-Informationen"; L="Logging- und Statusinformationen"; K="Information für KKS-Verfahren"
	D="Datenträgerspezifische Informationen"; I="Interne Nutzung"; A="Allgemeine Informationen"
	S="Informationen zur Verschlüsselung"
	NUMERISCH='[:digit:]'; BUCHSTABEN='[:alpha:]'; ALPHA_NUMERISCH='[:alnum:]'; ALPHA_NUMERISCH_PLUS='[:alnum:]@._%+-'
	# BEZEICHNUNG=(offset_bytes, length_bytes, art, valid_characters, 'muss_oder_kann', 'gültige Optionen als RegEx für grep', 'Beschreibung')
	IDENTIFIKATOR=(            0   6  "${A}"            ${NUMERISCH} 'muss'               '500000' 'Identifikator des Objektes "Krankenkassen-Kommunikation" Konstante 500000')
	VERSION=(                  6   2  "${A}"            ${NUMERISCH} 'muss'                   '01' 'Version der Auftragssatzstruktur. Ändert sich, wenn Felder des Auftragssatzes hinzugefügt, gelöscht oder geändert werden. '01': erste Version des Verfahren')
	LAENGE_AUFTRAG=(           8   8  "${A}"            ${NUMERISCH} 'muss'             '00000348' 'Länge der Auftragsdatei in Bytes (Objekt "Krankenkassen-Kommunikation") Bei VERSION = 01 steht hier als Konstante 00000348')
	SEQUENZ_NR=(              16   3  "${A}"            ${NUMERISCH} 'muss'                  '000' 'Laufende Nummer bei einer Teillieferung. Gibt die Sequenznummer der Datei an, sofern eine Nachricht auf mehrere Datenträger oder physikalische Dateien bei DFÜ verteilt werden muss. Zurzeit: 000 = Nachricht ist komplett vorhanden.')
	VERFAHREN_KENNUNG=(       19   5  "${R}"      ${ALPHA_NUMERISCH} 'muss' '(E|T)[A-Za-z0-9]{3}0' 'Das Feld VERFAHREN_KENNUNG ist in der Anlage A zur allgemeinen Dateistruktur im Datenaustausch festgelegt und unterliegt ständigen aufwärtskompatiblen Änderungen.')
	TRANSFER_NUMMER=(         24   3  "${A}"            ${NUMERISCH} 'muss'             '[0-9]{3}' 'Transfernummer nach Abschnitt 4.1')
	VERFAHREN_SPEZIFIKATION=( 27   5  "${R}"      ${ALPHA_NUMERISCH} 'kann'      '[ A-Za-z0-9]{5}' 'Weitere Spezifikation des Verfahrens innerhalb des in VERFAHREN_KENNUNG festgelegten Verfahrens.Die Werte werden eindeutig pro Verfahren (bei Datenaustausch z. B. der Nachrichtentyp, sofern eindeutig proLiefe-rung) festgelegt. Damit ist pro Verfahren eine weitere Unterscheidung der Nachrichtenart möglich. Dieses Feld kann benutzt werden, um die Verarbeitungspriorität auszudrücken. Sonst mit Blanks zu füllen.')
	ABSENDER_EIGNER=(         32  15  "${R}"      ${ALPHA_NUMERISCH} 'muss'             '[0-9]{9}' 'Absendender Eigner der Nutzdaten. Identifikation des Absenders.(IK: 9 Stellen) Der Eigner ist für die Korrektheit der Daten verantwortlich und nimmt die Verschlüsselung vor.')
	ABSENDER_PHYSIKALISCH=(   47  15  "${R}"      ${ALPHA_NUMERISCH} 'muss'             '[0-9]{9}' 'Tatsächlicher physikalischer Absender der Nutzdaten. Identifikation desselben Typs wie im Feld ABSENDER_EIGNER angegeben. Hier steht gegebenenfalls auch eine Datenübermittlungsstelle/Rechenzentrum.')
	EMPFAENGER_NUTZER=(       62  15  "${R}"      ${ALPHA_NUMERISCH} 'muss'             '[0-9]{9}' 'Empfänger, der die Daten nutzen soll. Identifikation desselben Typs wie im Feld ABSENDER_EIGNER angegeben.Dieser Empfänger ist im Besitz des Schlüssels, um verschlüsselte Informationen zu entschlüsseln. Der Nutzer nimmt die Weiterverarbeitung der Daten vor.(Zertifizierungs-IK laut Anhang 1)')
	EMPFAENGER_PHYSIKALISCH=( 77  15  "${R}"      ${ALPHA_NUMERISCH} 'muss'             '[0-9]{9}' 'Empfänger, der Daten physikalisch empfangen soll (= nächster Empfänger). Identifikation desselben Typs wie im Feld ABSENDER_EIGNER angegeben. Hier steht gegebenenfalls auch eine Datenübermittlungsstelle.(Datenannahme-IK laut Anhang 1)')
	FEHLER_NUMMER=(           92   6  "${R}"            ${NUMERISCH} 'muss'             '[0-9]{6}' 'Fehler-Nr. laut Fehlerkatalog bei Rücksendungen von Dateien. 000000: kein Fehler; gilt auch für Erstlieferung')
	FEHLER_MASSNAHME=(        98   6  "${R}"            ${NUMERISCH} 'muss'             '[0-9]{6}' 'Durchzuführende Maßnahme laut Fehlerkatalog. 000000: keine Maßnahme erforderlichSiehe Feld FEHLER_NUMMER. Gemäß dem Fehlerverfahren festzulegen.')
	DATEINAME=(              104  11  "${A}"      ${ALPHA_NUMERISCH} 'muss'     '[ A-Za-z0-9]{11}' 'Der vom Anwendungssystem vergebene (logische) Dateiname der zugehörigen Nutzdatendatei. (nach Abschn. 4.2) Im Datenaustausch nach §294 ff. SGB V sind die Dateinamen in den technischen Anlagen zu den vertraglichen Regelungen nach §294 ff. SGB V festgelegt.')
	DATUM_ERSTELLUNG=(       115  14  "${L}"            ${NUMERISCH} 'muss'            '[0-9]{14}' 'Erstellungsdatum der Datei aus der Anwendung. Format JJJJMMTTssmmss (Jahr, Monat, Tag, Stunde, Minute, Sekunde). Aus denFeldern ABSENDER_EIGNER, VERFAHREN_KENNUNG und DATUM_ERSTELLUNG kann ein eindeutiger Identifikator gebildet werden, anhand dessen eine Sendung eindeutig identifiziert werden kann. Es ist vom Absender-Eigner sicherzustellen, dass zwei unterschiedliche Sendn-gen nicht mit demselben Identifikator verschickt werden.')
	DATUM_UEBER_GESENDET=(   129  14  "${L}"            ${NUMERISCH} 'kann'            '[0-9]{14}' 'Start der Übermittlung der Datei (DFÜ). Format  JJJJMMTTssmmss (Jahr, Monat, Tag, Stunde, Minute, Sekunde). Diese Zeit kann als Logging-Information oder auch für Wiederaufsatzverfahren zwischen zwei Partnern genutzt werden. Wird vom Absender ausgefüllt. Keine DFÜ –das Feld ist vom Absender mit numerischen Nullen aufzufüllen')
	DATUM_UEBER_EMPF_START=( 143  14  "${L}"            ${NUMERISCH} 'kann'            '[0-9]{14}' 'Start des Empfangs der Datei (DFÜ). Format JJJJMMTT hhmmss (Jahr, Monat, Tag, Stunde, Minute, Sekunde). Wird nur vom ersten Empfänger ausgefüllt, der vertraglich die annehmende Stelle ist und deren Annahmezeit daher vertragliche Auswirkungen hat. Keine DFÜ –das Feld ist vom Absender mit numerischen Nullen aufzufüllen.')
	DATUM_UEBER_EMPF_ENDE=(  157  14  "${L}"            ${NUMERISCH} 'kann'            '[0-9]{14}' 'Ende der Empfangsübertragung der Datei. Format JJJJMMTT hhmmss (Jahr, Monat, Tag, Stunde, Minute, Sekunde). Wird vom Empfänger ausgefüllt.')
	DATEIVERSION=(           171   6  "${A}"            ${NUMERISCH} 'muss'               '000000' 'Versionsnummer der Datei. Wird derzeit in keinem Verfahren benutzt. Muss auf ‘000000’ gesetzt werden.')
	KORREKTUR=(              177   1  "${A}"            ${NUMERISCH} 'muss'                    '0' 'Ist bereits eine Datei mit derselben Dateiversion verschickt worden? ‘0’: Nein; ‘1’: Dies ist die Korrekturdatei. Die bereits erhaltene Datei kann gelöscht werden. Wird derzeit in keinem Verfahren benutzt. Muss auf ‘0’ gesetzt werden.')
	DATEIGROESSE_NUTZDATEN=( 178  12  "${A}"            ${NUMERISCH} 'muss'            '[0-9]{12}' 'Dateigröße der Nutzdatendatei in Bytes (unverschlüsselt und unkomprimiert)')
	DATEIGROESSE_UEBERTRAG=( 190  12  "${A}"            ${NUMERISCH} 'muss'            '[0-9]{12}' 'Dateigröße der übertragenen Nutzdatendatei in Bytes (Länge bei eventueller Verschlüsselung und Komprimierung)')
	ZEICHENSATZ=(            202   2  "${A}"      ${ALPHA_NUMERISCH} 'muss'           '(I1|I5|I7)' 'I5: ISO 8859-15 | I7: 7-Bit ASCII')
	KOMPRIMIERUNG=(          204   2  "${A}"            ${NUMERISCH} 'muss'              '(00|02)' '‘00’ keine; ‘02’ für LE-Verfahren aufgrund der TeleTrust-Definitionen')
	VERSCHLUESSELUNGSART=(   206   2  "${A}"            ${NUMERISCH} 'muss'                   '03' '‘03’ für LE-Verfahren im PKCS#7-Format')
	ELEKTRONISCHE_UNTERSCHR=(208   2  "${A}"            ${NUMERISCH} 'muss'                   '03' '‘03’ für LE-Verfahren im PKCS#7-Format')
	SATZFORMAT=(             210   3  "${D}"           ${BUCHSTABEN} 'kann'               '[ ]{3}' 'Satzformat der Datei auf dem Datenträger: F=FIX, V=Variabel, U=Undefiniert, FB=FIX_geblockt, FBA=FIX_geblockt_, VB=Variabel geblockt, ...Bei DFÜ: Konstante ‘   ‘.')
	SATZLAENGE=(             213   5  "${D}"            ${NUMERISCH} 'kann'                '00000' 'Satzlänge bei fixem Satzformat.Bei DFÜ: Konstante ‘00000’.')
	BLOCKLAENGE=(            218   8  "${D}"            ${NUMERISCH} 'kann'             '00000000' 'Blocklänge in Bytes, sofern geblockt. Bei DFÜ: Konstante ‘00000000’.')
	STATUS=(                 226   1  "${K}"      ${ALPHA_NUMERISCH} 'kann'             '[0-9]{1}' 'Bei Anlieferung durch das Abrechnungssystem: Leerzeichen Verarbeitungskennzeichnung (Anwendung, FTAM):0 Einstellung in Ordnung; 1 Ändern; 2 Suspendieren; 3 Löschen; 4 Übertragen; 5 Transferphase; 6 Keine Verbindung; 7 Fehlerhafter Transfer; 8 Statusabfrage')
	WIEDERHOLUNG=(           227   2  "${K}"            ${NUMERISCH} 'kann'             '[0-9]{2}' 'Hier wird die maximale Anzahl der Übertragungswiederholungen bei fehlerhaften Übertragungen angegeben.Wenn der angegebene Zähler überschritten wird, oder ein nicht-behebbarer Fehler beim Übertragungsversuch aufgetreten ist, wird der Auftrag als nicht durchführbar mit einem Diagnosecode gekennzeichnet')
	UEBERTRAGUNGSWEG=(       229   1  "${K}"            ${NUMERISCH} 'kann'             '[0-9]{1}' 'Mögliche Wege sind: 1 X.25; 2 ISDN; 3 ISDN, bei Übertragungsproblemen erneuter Versuch über X.2; 4 X.25, bei Übertragungsproblemenerneuter Versuch über ISDN; 5 anderer Weg')
	VERZOEGERTER_VERSAND=(   230  10  "${K}"            ${NUMERISCH} 'kann'            '[0-9]{10}' 'Hier wird der Zeitpunkt eingetragen, zu dem der Auftrag ausgeführt werden soll. Wird das Feld nicht vom Abrechnungssystem gefüllt oder ist der angegebene Ausführungszeitpunkt bereits überschritten, wird der Auftrag vom KKS zum nächstmöglichen Zeitpunkt ausgeführt. Im Format JJMMTTSSmm (Jahr, Monat, Tag, Stunde und Minute)')
	INFO_FEHLERFELDER=(      240   6  "${K}"            ${NUMERISCH} 'kann'             '[0-9]{6}' 'Fehlernummer aus FTAM. Bei erfolgreich ausgeführten Aufträgen ist das Feld leer.')
	VARIABLES_INFOFELD=(     246  28  "${K}"      ${ALPHA_NUMERISCH} 'kann'     '[ A-Za-z0-9]{28}' 'Klartextfehlermeldung. Bei erfolgreich ausgeführten Aufträgen ist das Feld leer (Blanks bei Datenträgern).')
	DATEINAME_PHYSIK=(       274  44  "${I}" ${ALPHA_NUMERISCH_PLUS} 'kann'  '[ A-Za-z0-9@._%+-]+' 'Verarbeitungsinterner physischer Dateiname')
	DATEI_BEZEICHNUNG=(      318  30  "${I}" ${ALPHA_NUMERISCH_PLUS} 'kann'    '[ A-Za-z0-9.]{30}' 'Variabler Bereich, um Zusatzinformationen zur Datei bereitzustellen')
	AUFTRAGSDATEI_BEZEICHNER_ARRAY=('IDENTIFIKATOR' 'VERSION' 'LAENGE_AUFTRAG' 'SEQUENZ_NR' 'VERFAHREN_KENNUNG' 'TRANSFER_NUMMER' 'VERFAHREN_SPEZIFIKATION' 'ABSENDER_EIGNER' 'ABSENDER_PHYSIKALISCH' 'EMPFAENGER_NUTZER' 'EMPFAENGER_PHYSIKALISCH' 'FEHLER_NUMMER' 'FEHLER_MASSNAHME' 'DATEINAME' 'DATUM_ERSTELLUNG' 'DATUM_UEBER_GESENDET' 'DATUM_UEBER_EMPF_START' 'DATUM_UEBER_EMPF_ENDE' 'DATEIVERSION' 'KORREKTUR' 'DATEIGROESSE'_'NUTZDATEN' 'DATEIGROESSE_UEBERTRAG' 'ZEICHENSATZ' 'KOMPRIMIERUNG' 'VERSCHLUESSELUNGSART' 'ELEKTRONISCHE_UNTERSCHR' 'SATZFORMAT' 'SATZLAENGE' 'BLOCKLAENGE' 'STATUS' 'WIEDERHOLUNG' 'UEBERTRAGUNGSWEG' 'VERZOEGERTER_VERSAND' 'INFO_FEHLERFELDER' 'VARIABLES_INFOFELD' 'DATEINAME_PHYSIK' 'DATEI_BEZEICHNUNG')
	ERLAUBTE_VERFAHREN=('(E|T)1Z10' '(E|T)KZV0' '(E|T)1VAX')
}
